import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [recipes, setRecipes] = useState([]);
  const [title, setTitle] = useState(""); // For recipe title
  const [description, setDescription] = useState(""); // For recipe description
  const [error, setError] = useState("");

  // Fetch recipes from the API when the component mounts
  useEffect(() => {
    fetch("http://localhost:8000/api/recipes/")
      .then((response) => {
        if (!response.ok) {
          return response.text().then((text) => {
            throw new Error(
              `HTTP error! status: ${response.status}. Message: ${text}`
            );
          });
        }
        return response.json();
      })
      .then((data) => setRecipes(data))
      .catch((error) => {
        console.error("Error fetching recipes:", error);
        setError("Failed to fetch recipes. Please try again later.");
      });
  }, []);

  // Handle adding a new recipe
  const handleAddRecipe = () => {
    if (!title.trim() || !description.trim()) {
      setError("Both title and description are required.");
      return;
    }

    fetch("http://localhost:8000/api/recipes/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ title, ingredients: description }), // Send both title and description
    })
      .then((response) => {
        if (!response.ok) {
          return response.text().then((text) => {
            throw new Error(
              `HTTP error! status: ${response.status}. Message: ${text}`
            );
          });
        }
        return response.json();
      })
      .then((data) => {
        setRecipes((prevRecipes) => [...prevRecipes, data]);
        setTitle(""); // Clear the title field
        setDescription(""); // Clear the description field
        setError("");
      })
      .catch((error) => {
        console.error("Error adding recipe:", error);
        setError("Failed to add recipe. Please try again later.");
      });
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Recipe List</h1>
        {error && <p className="error">{error}</p>}
        <ul className="recipe-list">
          {recipes.length > 0 ? (
            recipes.map((recipe) => (
              <li key={recipe.id} className="recipe-item">
                {recipe.title}: {recipe.ingredients}
              </li>
            ))
          ) : (
            <li className="recipe-item">No recipes found.</li>
          )}
        </ul>
        <div className="recipe-input">
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Recipe Title"
          />
          <input
            type="text"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Recipe Description"
          />
          <button onClick={handleAddRecipe}>Add Recipe</button>
        </div>
      </header>
    </div>
  );
}

export default App;
