import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [recipes, setRecipes] = useState([]);
  const [newRecipeTitle, setNewRecipeTitle] = useState("");
  const [newRecipeDescription, setNewRecipeDescription] = useState("");
  const [error, setError] = useState("");

  // Fetch recipes from the API when the component mounts
  useEffect(() => {
    fetch("http://localhost:8000/api/recipes/")
      .then((response) => {
        if (!response.ok) {
          return response.text().then((text) => {
            throw new Error(
              `HTTP error! status: ${response.status}. Message: ${text}`
            );
          });
        }
        return response.json();
      })
      .then((data) => setRecipes(data))
      .catch((error) => {
        console.error("Error fetching recipes:", error);
        setError("Failed to fetch recipes. Please try again later.");
      });
  }, []);

  // Handle adding a new recipe
  const handleAddRecipe = () => {
    if (!newRecipeTitle.trim() || !newRecipeDescription.trim()) {
      setError("Both title and description are required.");
      return;
    }

    fetch("http://localhost:8000/api/recipes/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        title: newRecipeTitle,
        ingredients: newRecipeDescription,
      }),
    })
      .then((response) => {
        if (!response.ok) {
          return response.text().then((text) => {
            throw new Error(
              `HTTP error! status: ${response.status}. Message: ${text}`
            );
          });
        }
        return response.json();
      })
      .then((data) => {
        setRecipes((prevRecipes) => [...prevRecipes, data]);
        setNewRecipeTitle("");
        setNewRecipeDescription("");
        setError("");
      })
      .catch((error) => {
        console.error("Error adding recipe:", error);
        setError("Failed to add recipe. Please try again later.");
      });
  };

  // Handle deleting all recipes
  const handleDeleteAllRecipes = () => {
    fetch("http://localhost:8000/api/recipes/", {
      method: "DELETE",
    })
      .then((response) => {
        if (!response.ok) {
          return response.text().then((text) => {
            throw new Error(
              `HTTP error! status: ${response.status}. Message: ${text}`
            );
          });
        }
        setRecipes([]);
        setError("");
      })
      .catch((error) => {
        console.error("Error deleting recipes:", error);
        setError("Failed to delete recipes. Please try again later.");
      });
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Recipe List</h1>
        {error && <p className="error">{error}</p>}
        <ul className="recipe-list">
          {recipes.length > 0 ? (
            recipes.map((recipe) => (
              <li key={recipe.id} className="recipe-item">
                <strong>{recipe.title}</strong>: {recipe.ingredients}
              </li>
            ))
          ) : (
            <li className="recipe-item">No recipes found.</li>
          )}
        </ul>
        <div className="recipe-input">
          <input
            type="text"
            value={newRecipeTitle}
            onChange={(e) => setNewRecipeTitle(e.target.value)}
            placeholder="Recipe title"
          />
          <input
            type="text"
            value={newRecipeDescription}
            onChange={(e) => setNewRecipeDescription(e.target.value)}
            placeholder="Recipe description"
          />
          <button onClick={handleAddRecipe}>Add Recipe</button>
          <button
            onClick={handleDeleteAllRecipes}
            className="delete-all-button"
          >
            Delete All Recipes
          </button>
        </div>
      </header>
    </div>
  );
}

export default App;
